    int x;
}Querys[maxn];
int find(int x){return (P[x]==x)?x:P[x]=find(P[x]);}
int main(){
    scanf("%d%d",&N,&M);
    memset(Cancel,0,sizeof(Cancel));
    Pre();
    for(int i=0;i<N;i++) P[i]=i;
    int u,v;
    for(int i=1;i<=M;i++){
        scanf("%d%d",&u,&v);
        Insert(u,v);
    }
    scanf("%d",&K);
    for(int i=1;i<=K;i++) {
        scanf("%d",&Querys[i].x);
        Cancel[Querys[i].x]=true;
    }
    for(int i=0;i<N;i++){
        if(!Cancel[i]){
            cntP++;
            int u=i;
            for(int j=head[u];j!=-1;j=Arcs[j].next){
                int v=Arcs[j].v;
                if(!Cancel[v]){
                    int xx=find(u);
                    int yy=find(v);
                    if(xx!=yy){
                        P[xx]=yy;
                        cntArc++;
                    }
                }
            }
        }
    }
    cntG=cntP-cntArc;
    ans[++pos]=cntG;
    for(int i=K;i>=1;i--){
        int u=Querys[i].x;
        Cancel[u]=false;
        cntP++;
        for(int i=head[u];i!=-1;i=Arcs[i].next){
            int v=Arcs[i].v;
            if(!Cancel[v]){
                int xx=find(u);
                int yy=find(v);
                if(xx!=yy){
                    P[xx]=yy;
                    cntArc++;
                }
            }
        }
        cntG=cntP-cntArc;
        ans[++pos]=cntG;
    }
    for(int i=pos;i>=1;i--){
        printf("%d\n",ans[i]);
    }
    return 0;
}
