//思路和下面题解一样，是个练逆向思维的好题，实现的时候我遇到的问题都注释了












```cpp
#include<cstdio>
#include<cstring>
#include<iostream>
using namespace std;
const int maxn=400000+10;
const int maxe=200000+10;
int n,m,k,cnt=0,tot;
int h[maxn],p[maxn],qq[maxn],vis[maxn],ans[maxn];
struct edge{
    int to,next;
}e[maxe*2];
void ins(int u,int v){
    e[++cnt]=(edge){v,h[u]};
    h[u]=cnt;
    e[++cnt]=(edge){u,h[v]};
    h[v]=cnt;
}
int inline read(){
    int num=0;
    char c;
    bool plus=true;
    while((c=getchar())==' '||c=='\n'||c=='\r');
    if(c=='-')plus=false;
    else num=c-'0';
    while(isdigit(c=getchar()))
        num=num*10+c-'0';
    return num*(plus?1:-1);
}
int find(int x){
    return p[x]==x?x:p[x]=find(p[x]);
}
void merge(int x){
    tot++;
    int r1=find(x),r2,y;
    for(int i=h[x];i;i=e[i].next){
        y=e[i].to;
        if(!vis[y]){
            r2=find(y);
            if(r1!=r2){
                p[r2]=r1;//不能反过来，不然不是一个集合 
                tot--;
            }
        }
    }
    vis[x]=0;
}
int main(){
    n=read(); m=read();
    for(int i=1;i<=m;i++){
        int x,y;
        x=read();y=read();
        x++; y++; //下面有说明 
        ins(x,y);
    }
    k=read();
    for(int i=1;i<=k;i++){
        qq[i]=read();
        qq[i]++;
        vis[qq[i]]=1;
    }
    for(int i=1;i<=n;i++)p[i]=i;//n有0，从0开始，注意！！ 或者前面每个点都加一 
    tot=0;//tot=n-k也可以，上面不用每次都加，星球被炸之后不能算作联通快 
    for(int i=1;i<=n;i++){
        if(!vis[i]) //勿忘 
            merge(i);
    }
    ans[k+1]=tot;
    for(int i=k;i>=1;i--){
        //vis[qq[i]]=0;
        //tot++;
        merge(qq[i]);
        ans[i]=tot;
    }
    for(int i=1;i<=k+1;i++)
        printf("%d\n",ans[i]);
    return 0;
} 

