  for(int i=1;i<=n;i++)
  {
    int c1=0,c2=0;
    for(int j=1;j<=n;j++)
    {
      if(T[i][j]>T[i][c1])
      {
        c2=c1;
        c1=j;
        continue;
      }//C1为最大值
      if(T[i][j]>T[i][c2])c2=j;//次大值
