bool isRev(int x){//判断是否为回文数的代码
	int t = x;
	int value = 0;
	while (x / 10){
		value = 10 * value + x % 10;
		x /=  10;
	}
	if(value*10 + x == t) return 1;
	else return 0;
}

int main()
{
	int a,b;
	cin>>a>>b;
    //投机取巧，将a变为奇数，将很大的b变为9999999，每次循环＋2，新判断是不是回文数，在判断是不是素数
	if(a%2==0) a+=1;
	if(b>9999999) b=9999999;
	for(int i=a;i<=b;i+=2){
		if(isRev(i)){
			if(isprime(i)){
				cout<<i<<endl;
			}
		}
	}
