```cpp
#include <stdio.h>
#include <math.h>

class binInt
{
public:
	binInt();
	void set(int n);
	int get();
	int getLenth();
	void invert();           // invert the num[] and change to num[]
	int checkhuiwen();
	int checksushu();
	void madehuiwen(int mode);	// 1 is HUiwen include middle num，0 is mirror huiwen
private:
	int number;		// save num mode
	int num[20];	// save num[] mode
	int length;		// the length is real length
	void shuzuhua();// let the num mode to num[]mode
	void shuzihua();// let the num[] mode to num mode
};
binInt::binInt()
{
	number = 0;
	length = 0;
}
void binInt::set(int n)
{
	number = n;
	length = 0;
	shuzuhua();
}
int binInt::get()
{
	return number;
}
int binInt::getLenth()
{
	return length;
}
void binInt::invert()
{
    for (int s = 0; s < length / 2; s++)
    {
        int temp = num[s];
        num[s] = num[length - 1 - s];
        num[length - 1 - s] = temp;
    }
    shuzihua();
}
int binInt::checkhuiwen()
{
	for (int s = 0; s < length; s++)
	{
		if (num[s] != num[length - 1 - s])
		{
			return 0;
		}
	}
	return 1;
}
int binInt::checksushu()
{
	for (int s = 2; s <= sqrt(number); s++)
	{
		if (number % s == 0)
		{
			return 0;
		}
	}
	return 1;
}
void binInt::shuzuhua()
{
	int n = number;
	for (int s = 0; ; s++)
	{
		if (n == 0)
		{
			break;
		}
		int temp;
		temp = n % 10;
		length++;
		num[s] = temp;
		n /= 10;
	}
	invert();
}
void binInt::shuzihua()
{
	number = 0;
	for (int s = 1, len = 0; len < length; s *= 10, len++)
	{
		number += num[len] * s;
	}
}
void binInt::madehuiwen(int mode)
{
	if (mode == 0)
	{
		length *= 2;
		for (int s = 0; s < length / 2; s++)
		{
			num[length - 1 - s] = num[s];
		}
	}
	if (mode == 1)
	{
		length *= 2;
		length--;
		for (int s = 0; s < length / 2; s++)
		{
			num[length - 1 - s] = num[s];
		}
	}
	shuzihua();
}

int main(void)
{
    int a, b;
    scanf("%d%d", &a, &b);
    binInt num;
    for (int s = 1; true; s *= 10)
    {
        // first run the 1 mode huiwen
        int numLength = -1;
        for (int u = s; true; u++)
        {
            num.set(u);
            if (numLength == -1)
                numLength = num.getLenth();
            if (numLength != -1 && numLength < num.getLenth())
                break;
            num.madehuiwen(1);
            if (num.get() > b)
                return 0;
            if (num.checksushu() == 1 && num.get() >= a)
            {
                printf("%d\n", num.get());
            }
        }
        numLength = -1;
        // Second run the 0 mode huiwen
        for (int u = s; true; u++)
        {
            num.set(u);
            if (numLength == -1)
                numLength = num.getLenth();
            if (numLength != -1 && numLength < num.getLenth())
                break;
            num.madehuiwen(0);
            if (num.get() > b)
                return 0;
            if (num.checksushu() == 1 && num.get() >= a)
            {
                printf("%d\n", num.get());
            }
        }
    }

    return 0;
}
