# include<stdio.h>
# include<stdlib.h>
int w[100000001],o[100000001];
void qsort(int l,int r)//定义快排
{
    int i,j,k,t;
    i=l;
    j=r;
    t=w[(l+r)/2];
    do{
        while(w[i]>t) i++;
        while(w[j]<t) j--;
        if(i<=j)
        {
            k=w[i]; w[i]=w[j];    w[j]=k; 
            k=o[i]; o[i]=o[j];    o[j]=k; 
        i++;j--;
        }
    }while(i<=j);
    if(l<j)qsort(l,j);
    if(i<r)qsort(i,r);
}
int main()
{
    int n,i,s=0;
    double j=0.0;//用Int的话只能拿80分
    scanf("%d",&n);
    for(i=1;i<=n;i++)//读入
    {
        scanf("%d",&w[i]);
        o[i]=i;
    }
    qsort(1,n);//调用快排
    for(i=n;i>=1;i--)
    {
        printf("%d ",o[i]);//顺序输出
        j=j+s;//s中是每个人的等待时间
        s=s+w[i];//这句一定要写在后面
    }
    printf("\n");
    printf("%.2f",j*1.0/n);//输出
}
```

