	while(i<=j)
	{
		while(a[i]<mid)
		{
			i++;
		}
		while(a[j]>mid)
		{
			j--;
		}
		if(i<=j)
		{
			swap(a[i],a[j]);
			swap(b[i],b[j]);//交换一下数据 
			i++;j--;
		}
	}
	if(l<j)
	{
		qsort(l,j);//递归 
	}
	if(i<r)
	{
		qsort(i,r);//同上 
	}
}
int main()
{
	cin>>n;
	for(int i=0;i<n;i++)//输入 
	{
		cin>>a[i];
		b[i]=i+1;
	}
	n--;
	qsort(0,n);//排序 
	for(int i=0;i<=n;i++)
	{
		cout<<b[i]<<" ";
		ave+=(a[i]*(n-i));//算总时间 
	}
	ave/=(n+1);//求平均值 
	cout<<endl;
	cout<<fixed<<setprecision(2)<<ave;//输出fixed，setprecision函数在<cstdio>下 
	return 0;//鬼畜 
}
