            a[j+1]:=a[j];
            b[j+1]:=b[j];
            dec(j);
        end;
        a[j+1]:=x;
        b[j+1]:=i;    
    end;{边读边排序，实际上就是插入排序的改编}
    for i:=1 to n do begin 
        write(b[i],' ');
        y:=y+a[i];{统计时间（地球人都懂）}
        s:=s+y-a[i];{自己排队的时间不算}
    end;
    writeln;
    write(s/n:0:2);{输出，保留两位小数}
end.
