    {  
    int a; cin >>a; int b=a,s[a-1],d[a-1]; for (int q=0;q<a;q++) {scanf("%d",&s[q]); d[q]=s[q];}      
    sort (d,d+a);  
    for (int q=0;q<a-1;q++)  
    for (int w=0;w<a;w++)  
    if (s[w]==d[q]){cout <<w+1<<" "; s[w]=-1; break;}  
    for (int w=0;w<a;w++)  
    if (s[w]==d[a-1]){cout <<w+1<<endl; s[w]=-1;break;}  
    float z=0.0;  
    for (int w=0;w<b;w++)  
    {z+=d[w]*(a-1); a--;}  
    printf("%0.2f",z/b);  
