#include <bits/stdc++.h>
#define N 100005
#define M 105
using namespace std;

template <typename T>

void read(T &a)
{
	T x = 0,f = 1;
	char ch = getchar();
	while (ch < '0' || ch > '9')
	{
		if (ch == '-') f = -1;
		ch = getchar();
	}
	while (ch >= '0' && ch <= '9')
	{
		x = (x << 1) + (x << 3) + (ch ^ 48);
		ch = getchar();
	}
	a = x * f;
}

template <typename T>

void write(T x)
{
	if (x < 0) putchar('-'),x = -x;
	if (x < 10) return (void) putchar(x + '0');
	write(x / 10);
	putchar(x % 10 + '0');
}

template <typename T>

void writeln(T x)
{
	write(x);
	putchar('\n');
}

template <typename T>

void writes(T x)
{
	write(x);
	putchar(' ');
}

template <typename T,typename... Args> 

void read(T &maxx,Args &... args)
{
	read(maxx);
	read(args...);
}

template <typename T,typename... Args>

void writes(T maxx,Args ... args)
{
	writes(maxx);
	writes(args...);
}

template <typename T,typename... Args>

void writeln(T maxx,Args ... args)
{
	writes(maxx);
	writes(args...);
	putchar('\n');
}

int n,d,k,a[N][M],id[N];

namespace sub1
{
	int sum[M];
	int clac(int x)
	{
		int ans = 0;
		for (int i = 1; i <= d; i++)
			ans += sum[i] * a[x][i];
		return ans % k;
	}
	int check(int x,int y)
	{
		int ans = 0;
		for (int i = 1; i <= d; i++)
			ans += a[x][i] * a[y][i] % k,ans %= k;
		return ans;
	}
	signed main()
	{
		for (int i = 1; i <= d; i++)
			sum[i] = a[1][i];
		for (int i = 2; i <= n; i++)
		{
			if (clac(i) == (i - 1) % k)
			{
				for (int j = 1; j <= d; j++)
					sum[j] = sum[j] + a[i][j];
				continue;
			}
			for (int j = 1; j < i; j++)
			{
				if (!check(i,j))
				{
					writeln(j,i);
					return 0;
				}
			}
		}
		puts("-1 -1");
		return 0;
	}
}

namespace sub2
{
	int sum[M][M];
	int clac(int x)
	{
		int ans = 0;
		for (int i = 1; i <= d; i++)
			for (int j = 1; j <= d; j++)
				ans = (ans + sum[i][j] * a[id[x]][i] % k * a[id[x]][j] % k) % k;
		return ans;
	}
	int check(int x,int y)
	{
		int ans = 0;
		for (int i = 1; i <= d; i++)
			ans += a[id[x]][i] * a[id[y]][i] % k,ans %= k;
		return ans;
	}
	signed main()
	{
		for (int i = 1; i <= d; i++)
			for (int j = 1; j <= d; j++)
				sum[i][j] = a[id[1]][i] * a[id[1]][j] % k;
		for (int i = 2; i <= n; i++)
		{
			if (clac(i) == (i - 1) % k)
			{
				for (int j = 1; j <= d; j++)
					for (int kk = 1; kk <= d; kk++)
						sum[j][kk] += a[id[i]][j] * a[id[i]][kk] % k,sum[j][kk] %= k;
				continue;
			}
			for (int j = 1; j < i; j++)
			{
				if (!check(i,j))
				{
					if (id[i] > id[j]) swap(id[i],id[j]);
					writeln(id[i],id[j]);
					exit(0);
				}
			}
			assert(0);
		}
		return 0;
	}
}

signed main()
{
	read(n,d,k);
	for (int i = 1; i <= n; i++)
		for (int j = 1; j <= d; j++)
			read(a[i][j]),a[i][j] %= k;
	if (k == 2) sub1::main();
	else
	{
		for (int i = 1; i <= n; i++)
			id[i] = i;
		for (int i = 1; i <= 20; i++)
		{
			random_shuffle(id + 1,id + n + 1);
			sub2::main();
		}
		puts("-1 -1");
	}
	return 0;
}
