#include<iostream>  
#include<cstdio>  
#include<cstring>  
#include<cstdlib>  
using namespace std;  
const int N = 1E5 + 10;  
int n,d,k,pos,g[N],Z[N],W[N],a[N][111],T[N],A[N],B[N];  
int Mul_Vector (int x,int y)  
{  
    int ret = 0,*nx = a[x],*ny = a[y];  
    for (int i = 1; i <= d; i++)  
        ret += nx[i]*ny[i];
    return ret%k;  
} 
void Print()  
{  
    for (int i = 1; i <= n; i++)  
        if (i != pos && Mul_Vector(pos,i) == 0)  
            {printf("%d %d\n",min(i,pos),max(i,pos)); return;}  
}  
void Solve2()  
{    
    for (int I = 1; I <= 10; I++)  
    {
        int tot = 0;  
        for (int i = 1; i <= n; i++)  
            T[i] = rand()%k,tot += T[i];  
        tot %= k;  
        for (int i = 1; i <= d; i++) W[i] = 0;  
        for (int i = 1; i <= n; i++) Z[i] = 0;
        for (int j = 1; j <= n; j++)
        {
            int *na = a[j];
            for (int i = 1; i <= d; i++) W[i] += T[j]*na[i];
        }  
        for (int i = 1; i <= d; i++) W[i] %= k;
        for (int i = 1; i <= n; i++)  
        {  
            int *na = a[i];
            for (int j = 1; j <= d; j++) Z[i] += W[j]*na[j];  
            Z[i] += (1-g[i])*T[i]; Z[i] %= k; Z[i] += k;  
            if (Z[i] % k != tot) {pos = i; Print(); return;}  
        }  
    }
    puts("-1 -1");  
}  
void Solve3()  
{  
    for (int i = 1; i <= n; i++) g[i] = g[i]*g[i]%k;
    int D = d*d;
    for (int i = 1; i <= D; i++)
    {
        int t = (i % d == 0)?i/d:i/d+1;
        A[i] = t; B[i] = i-(t-1)*d;
    }
    for (int I = 1; I <= 10; I++) 
    {
        int tot = 0;  
        for (int i = 1; i <= n; i++)  
            T[i] = rand()%k,tot += T[i];  
        tot %= k;  
        for (int i = 1; i <= D; i++) W[i] = 0;  
        for (int i = 1; i <= n; i++) Z[i] = 0;  
        for (int j = 1; j <= n; j++)
        {
            int *na = a[j];
            for (int i = 1; i <= D; i++) W[i] += T[j]*na[A[i]]*na[B[i]];
        }
        for (int i = 1; i <= D; i++) W[i] %= k;
        for (int i = 1; i <= n; i++)  
        {  
            int *na = a[i];
            for (int j = 1; j <= D; j++) Z[i] += W[j]*na[A[j]]*na[B[j]]; 
            Z[i] += (1-g[i])*T[i]; Z[i] %= k; Z[i] += k;  
            if (Z[i] % k != tot) {pos = i; Print(); return;}  
        }  
    }
    puts("-1 -1");  
}  
