    n = getint(); d = getint(); k = getint();
    for (int i = 1; i <= n; i++)  
        for (int j = 1; j <= d; j++)  
            a[i][j] = getint() % k;  
    for (int i = 1; i <= n; i++)
        for (int j = 1; j <= d; j++)
            g[i] += a[i][j]*a[i][j];
    for (int i = 1; i <= n; i++) g[i] %= k;
    if (k == 2) Solve2();  
