long long mod;
long long s(long long x,long long y){
	for(;y;y^=y&-y)x+=y&-y,x%=mod;
	return x;
}
long long m(long long x,long long y){
	long long sum=0;
	for(;y;y>>=1,x=s(x,x))if(y&1)sum=s(sum,x),sum%=mod;
	return sum;
