procedure Power;     //快速幂
begin
    t:=1;y:=a;b:=p;
    while b>0 do
        begin
            if b and 1=1     //b and 1=1说明为奇数
                then t:=t*y mod k;
            y:=y*y mod k;
            b:=b shr 1;
        end;
end;
procedure Main;
begin
    readln(a,p,k);
end;
begin
    Main;
    Power;
    Print;
end. 

