int f(int p){
    if(p==0){//b^0%k=1
        return 1;
    }
    int tmp=f(p/2)%k;
    tmp=(tmp*tmp)%k;//b^p%k=(b^(p/2))^2%k
    if(p%2==1){
        tmp=(tmp*b)%k;//如果p为奇数,则b^p%k=((b^(p/2))^2)*b%k;
    }
    return tmp;
}
