long long b,p,k,ans; 
long long getMode(long long int x,long long int y){//x^y
    if(y==1)return x%k;
    else{
        if(y%2==0) {
            int temp=getMode(x,y/2);
            return temp*temp%k;
        }
        else
            return (getMode(x,(y+1)/2)*getMode(x,(y-1)/2))%k;
    }
}
int main(){
    cin>>b>>p>>k;
    ans=getMode(b,p);
    cout<<b<<"^"<<p<<" mod "<<k<<"="<<ans<<endl;
    return 0;
}
