#include<algorithm> 
#include<queue>
using namespace std;
priority_queue<int>s;
struct node
{
	int t,v;
}a[505];
bool cmp(const node x,const node y)
{
	return x.t > y.t;
}

int main()
{
	int n,m;
	scanf("%d%d",&m,&n);
	for(int i = 1;i <= n;++ i)
		scanf("%d",&a[i].t);
	for(int i = 1;i <= n;++ i)
		scanf("%d",&a[i].v);
	sort(a + 1,a + 1 + n,cmp);
	int js = 1;
	for(int i = n;i >= 1;i --)
	{
		while(a[js].t >= i)
		{
			s.push(a[js].v);
			js ++;
		}
		if(!s.empty())
		s.pop();
	}
	while(!s.empty())
	{
		m -= s.top();
		s.pop();
	}
	cout << m << endl;
	return 0;
}
