	for(i=1;i<=n;i++)
		for(j=xy[i].time;j>=1;j--)//寻找限定时间前是否有时间段空着
		if(c[j]==0)//如果有，则把这个时间段设为已使用
		{
			c[j]=1;
			xy[i].money=0;//获得这个游戏的钱
			break;
		}
		for(i=1;i<=n;i++)
		q=q-xy[i].money;//减去所以为完成游戏的钱数
		cout<<q<<endl;//输出
}
