	for(int i=1; i<=n; i++) {
		for(int j=1; j<=m; j++) {
			if(g[i].d<j) f[i][j]=f[i][j-1];
			else f[i][j]=max(f[i-1][j-1]+g[i].a,f[i-1][j]);
		}
	}
	cout<<p-s+f[n][m];
	return 0;
}
