			if(c[i][j]=='h')
			{
				if(c[i][j+1]=='e'&&c[i][j+2]=='h'&&c[i][j+3]=='e') ans++;//向右再搜一次；
				if(c[i-1][j]=='e'&&c[i-2][j]=='h'&&c[i-3][j]=='e') ans++;//向上再搜一次；
				if(c[i+1][j]=='e'&&c[i+2][j]=='h'&&c[i+3][j]=='e') ans++;//最后再向下搞一次，完事；
			}
		}
	}
	cout<<ans<<endl;
