```cpp
#include<bits/stdc++.h>
using namespace std;

bool flag=false;
bool ma[15][15];
int a[15][15];
int newx[]={0,-1,0,1};//注意题目描述,顺序左上右下
int newy[]={-1,0,1,0};
int s,t;
int qq=0;
pair<int,int> p;//pair用来储存坐标
stack<pair<int,int> > q;//q表示目前的从起点开始的所有的坐标的stack
stack<pair<int,int> > st;//倒瓶stack,下面会说

void dfs(int x, int y)
{
    ma[x][y]=true;
    p.first=x;
    p.second=y;
    q.push(p);//这里可以用make_pair代替
    if(x==s&&y==t)
    {
        flag=true;
        if(qq!=0) cout<<endl;
        qq++;//这个可以无视233，纯粹只是为了让最后一行不输出回车
        while(!q.empty())
        {
            st.push(q.top());//先将q的所有元素倒瓶到st里,保证有序
            q.pop();
        }
        while(st.size()>1)//因为起点是没有->的,所以>1
        {
            cout<<'('<<st.top().first<<','<<st.top().second<<')'<<"->";//有序输出
            q.push(st.top());//再倒回到q栈里，回复原有序列，即栈顶为最新到达的坐标（当然，这里终点就不放进去了）
            st.pop();//清空st栈
        }
        cout<<'('<<st.top().first<<','<<st.top().second<<')';
        st.pop();//终点输出完毕
        ma[x][y]=false;
        return;        
    }
    for(int i=0;i<4;i++)//标准DFS
    {
        int nx=x+newx[i];
        int ny=y+newy[i];
        if(a[nx][ny]==1 && ma[nx][ny]==false)
        {
            dfs(nx,ny);
        }
    }
    q.pop();//别忘了这里需要回溯清空
    ma[x][y]=false;
    return;
}

int main()
{
    int n,m;
    scanf("%d%d",&m,&n);
    for(int i=0;i<=(n+1);i++)//不用判断边界的写法
    {
        ma[0][i]=true;
        ma[m+1][i]=true;
    }
    for(int i=0;i<=(m+1);i++)//不用判断边界的写法x2
    {
        ma[i][0]=true;
        ma[i][n+1]=true;
    }
    for(int i=1;i<=m;i++)
    {
        for(int j=1;j<=n;j++)
        {
            scanf("%d",&a[i][j]);
        }
    }
    int x,y;
    scanf("%d%d",&x,&y);
    scanf("%d%d",&s,&t);
    dfs(x,y);
    if(flag==false) cout<<-1;//flag标记
    return 0;
}
