}//输出函数,自己写的
void queue_pop()
{
	for(reg int i=1;i<road.size();++i)
	road.push(road.front()),road.pop();
	road.pop();
}//将队尾弹出
int dir[4][2]={{0,-1},{-1,0},{0,1},{1,0}};
//分别代表左,上,右,下
void dfs(int x,int y)
{
	if(in(x,y)) return;
	if(x==ex and y==ey) {b=1; print(); queue_pop(); return;}
	road.push(per(x,y));
    //记下这个坐标可以走
