void dfs(int x, int y)//标准dfs
{
	if(x == fx && y == fy)//到达终点
	{
		print();//打印
		return;
	}

	if(x - 1 >= 1 && a[x - 1][y] && !vis[x - 1][y])//向左走
	{
		v1.push_back(x - 1);//vector中加入选择的坐标
		v2.push_back(y);//别忘了纵坐标
		vis[x - 1][y] = true;//设置走过
		dfs(x - 1, y);//递归dfs
		vis[x - 1][y] = false;//复原
		v1.pop_back();//别忘了弹出这个点的坐标
		v2.pop_back();
	}
	//以下除了方向不同 其他的都类似
	if(y - 1 >= 1 && a[x][y - 1] && !vis[x][y - 1])
	{
