		v2.push_back(y - 1);
		vis[x][y - 1] = true;
		dfs(x, y - 1);
		vis[x][y - 1] = false;
		v1.pop_back();
		v2.pop_back();
	}

	if(y + 1 <= n && a[x][y + 1] && !vis[x][y + 1])
	{
