    ansx[t+1]=x,ansy[t+1]=y;//记录路径 
    visit[x][y]=1;//标记 
    if(!visit[x][y-1]&&y-1>=1&&map[x][y-1]==1)dfs(x,y-1,t+1);
    if(!visit[x-1][y]&&x-1>=1&&map[x-1][y]==1)dfs(x-1,y,t+1);
