        cin>>finish_x>>finish_y;//读入终点位置。
        visit[start_x][start_y]=true;//当前初始位置标记为经过了。
        search(start_x,start_y,0);//开始搜索。
        if(!flag)cout<<-1<<endl;//若没有一条完整的路线则输出-1。
    return 0;
}
