			}
		}
		if(i==1)
		{
			t[i]=n/o[i+1];
		}
		s=s%o[i];
	}//读取每一位上的数字，并且判断其贡献
	for(int i=1;i<=c;i++)
	{
		ans[0]=ans[0]+t[i];
	}
```


