        f[1][i][i]=1;
    for (int i=2;i<=15;i++)
    {
        sum=sum*10;
        f[i][0][0]=f[i-1][1][0]*9+f[i-1][0][0]+sum;
        for (int k=1;k<=9;k++)
            f[i][0][k]=f[i-1][0][k]*9+f[i-1][k][k];
        for (int j=1;j<=9;j++)
        {
            f[i][j][0]=f[i-1][1][0]*9+f[i-1][0][0];
            for (int k=1;k<=9;k++)
            {
                if (j==k)
                    f[i][j][k]=f[i-1][0][k]*9+f[i-1][k][k]+sum;
                else 
                    f[i][j][k]=f[i-1][0][k]*9+f[i-1][k][k];
            }    
        }
    }
    bin[1]=1;
    for (int i=2;i<=10;i++)
