        memset(this->s, 0, sizeof(this->s));
    }
    void merge() {
        int i = 0;
        if (l) s[i++] = l;
        if (m) s[i++] = m;
        if (r) s[i++] = r;
        s[i] = '\0';
    }
}s[MAX];

struct stack {
    static const int MAX = 100 + 5;
    int val[MAX];
    int _top;

    stack() { 
        memset(this->val, 0, sizeof(this->val)); 
        this->_top = 0; 
    }

    void push(int x) { this->val[++this->_top] = x; }
    void pop() { this->_top--; if (_top < 0) _top = 0; }
    int top() { return this->val[this->_top]; }
}s2;

char in[MAX];

int main() {
    scanf("%s", in);
    int len = strlen(in);
    for (int i = 0; i < len; ++i)
