        switch (s[i].m) {
            case '(': s2.push(i); break;
            case '[': s2.push(i); break;
            case ')': 
                if (s[s2.top()].m == '(') s2.pop();
                else s[i].l = '('; 
                break;
            case ']':
                if (s[s2.top()].m == '[') s2.pop();
                else s[i].l = '[';
                break;
        }
    }
    while (s2._top > 0) {
        switch (s[s2.top()].m) {
            case '(': s[s2.top()].r = ')'; break;
            case '[': s[s2.top()].r = ']'; break;
        }
