    int l=s.size();
    for (int i=0;i<l;i++)
    {
        if (s[i]=='(') 
		{
		   q.push(i); //入栈
		   b[i]=')';//相应的括号匹配上
		}
        if (s[i]=='[') 
		{
		   q.push(i); //同理
		   b[i]=']';
		}
        if (s[i]==')' or s[i]==']')//如果是后半边括号
            if (q.empty() or b[q.top()]!=s[i])//这里一定是q.empty不要写错了
                if (s[i]==')') 
				    b[i]='('; 
				else 
