    for (int i=0;i<l;i++)
    {
        if (b[i]=='(' or b[i]=='[')
            cout<<b[i];//输出配对
        cout<<s[i];//输出原来的字符
        if (b[i]==')'||b[i]==']') 
		    cout<<b[i];
