void judge() {
	stack<int> st;
	int i,l,j=0;
	scanf("%s",s);
	l=strlen(s);
	fh['(']=-1;fh[')']=1;fh['[']=-2;fh[']']=2;fh['{']=-3;fh['}']=3;fh['<']=-4;fh['>']=4;
	for (i=0; i<l; i++) {
		char c=s[i];
		if(fh[c]<0) st.push(i);//左括号入栈
		else {
			if(!st.empty()){
				int k=st.top();	
