	for(int i=0; i<l; i++)
		if(b[i])cout<<s[i];
		else{
			int k=fh[s[i]];
			if(k<0)cout<<s[i]<<tj[4+k];
			else cout<<tj[4+k]<<s[i];
		}
}
int main() {
	judge();
}
