using namespace std;

namespace my
{
    int now[112], dest[112];
    int other[4][4];
    int n;

    void init()
    {
        other[1][2] = 3;
        other[2][1] = 3;
        other[1][3] = 2;
        other[3][1] = 2;
        other[2][3] = 1;
        other[3][2] = 1;
    }

    void read(int *dest)
    {
        for (int i(1); i <= 3; ++i)
        {
            int k;
            scanf("%d", &k);
            for (int j(0); j != k; ++j)
            {
                int in;
                scanf("%d", &in);
                dest[in] = i;
            }
        }
    }


    int cnt;

    void OldHnoi(int rank, int to)//将编号小于K的盘全部移动到柱子to上
    {
        if (rank <= 0) return;
        if (now[rank] == to)
        {    
            OldHnoi(rank - 1, to);
            return;
        }
        OldHnoi(rank - 1, other[now[rank]][to]);
        printf("move %d from %c to %c\n", rank, 'A' + now[rank] - 1, 'A' + to - 1);
        now[rank] = to;
        ++cnt;
        OldHnoi(rank - 1, to);
    }

    //将编号为rank的盘移动到to上，而编号小于rank的盘子会被移动到other[now[rank]][to]上
    void move(int rank, int to)
    {
        if (rank <= 0) return;
        if (now[rank] == to)
        {    
            OldHnoi(rank - 1, to);
            return;
        }
        OldHnoi(rank - 1, other[now[rank]][to]);
        printf("move %d from %c to %c\n", rank, 'A' + now[rank] - 1, 'A' + to - 1);
        now[rank] = to;
        ++cnt;
    }
}

int main()
{
    my::init();
    scanf("%d", &my::n);
    my::read(my::now);
    my::read(my::dest);
    for (int i(my::n); i != 0; --i)
    {
        if (my::now[i] != my::dest[i])
        {
            my::move(i, my::dest[i]);
        }
    }
    printf("%d\n", my::cnt);
    return 0;
}
