#include<bits/stdc++.h>
#define mod 100007
#define N 1000000
using namespace std;
inline int read(){
    int x=0; int sign=1; char c=getchar();
    while(c>'9' || c<'0') {if (c=='-') sign=-1;c=getchar();}
    while(c>='0' && c<='9'){x=(x<<3)+(x<<1)+c-'0';c=getchar();}
    return x*sign;
}

int cnt;
struct state{
    int s[3][46];
    int size[3];
    int num; int fa; 
	int step; int esti; 

    void input(){
        memset(s, 0, sizeof(s));
        step=esti=num=fa=0;
        for(int i=0; i<3; i++){
            size[i]=read();
            for(int j=1; j<=size[i]; j++){
                s[i][j]=read();
            }
        }
    }

    bool operator== (const state &b) const{
        for(int i=0; i<3; i++){
            if(size[i]!=b.size[i]) return false;
        }
        for(int i=0; i<3; i++){
            for(int j=1; j<=size[i]; j++){
                if(s[i][j]!=b.s[i][j]) return false;
            }
        }
        return true;
    }

    bool operator< (const state &b) const{
		return step+esti>b.step+b.esti;
	}


    int hash(){
        int ans=0;
        for(int i=0; i<3; i++){
            for(int j=1; j<=size[i]; j++){
                ans+=(i+1)*j*s[i][j];
                ans*=13331;
            }
        }
        ans=(ans%mod)+mod; ans%=mod;
        return ans;
    }

    void show(){
        for(int i=0; i<3; i++){
            cout<<size[i]<<" ";
        }
        cout<<endl;
        for(int i=0; i<3; i++){
            for(int j=1; j<=size[i]; j++){
                cout<<s[i][j]<<" ";
            }
            cout<<endl;
        }
    } 

}sta, fin;

int n;
vector<int> hash[mod];
state lib[N]; int libsize;

int estimate(state &u){
	int ans=0; //多少方块不在其位
	for(int i=0; i<3; i++){
		for(int j=1; j<=u.size[i]; j++){
			if(u.s[i][j]!=fin.s[i][j] && u.s[i][j]) ans++;  
		}
	}
	return ans;
}

void show_path(int i){
	state &u=lib[i];
	if(u.num==0) return ;
	show_path(u.fa);

	state &w=lib[u.fa];
	int x, y, z;
	for(int i=0; i<3; i++){
		if (w.size[i]>u.size[i]) {
			x=i;
			z=w.s[i][w.size[i]];
		}
		if (w.size[i]<u.size[i])
			y=i;
	}
	printf("move %d from %c to %c\n", z, x+'A', y+'A');

}



state move(state u, int x, int y){
    int &sx=u.size[x], &sy=u.size[y];
    sy++;
    u.s[y][sy]=u.s[x][sx];
    u.s[x][sx]=0;
    sx--;
    return u;
    //step++;
}

void add_lib(state &u){
	u.num=libsize;
	hash[u.hash()].push_back(u.num);
	lib[libsize++]=u;
}

void bfs(){
    priority_queue<state> q;
    add_lib(sta);
    q.push(sta);
    while(!q.empty()){
        //cout<<"working"<<endl;
        state u=q.top(); q.pop();
        if(u==fin){
//        	u.show();
//        	cout<<u.num<<endl;
            show_path(u.num);
            cout<<u.step; 
            break;
        }
        for(int x=0; x<3; x++){
            for(int y=0; y<3; y++){
                int sx=u.size[x], sy=u.size[y];
                if (x==y || sx==0 || (sy!=0 && u.s[x][sx]>u.s[y][sy])) continue;
                state v=move(u, x, y);
                int hv=v.hash();
                bool flag=1;
                for(int i=0; i<hash[hv].size(); i++){
                    if(v==lib[ hash[hv][i] ]) {
                        flag=0;
                        break;
                    }
                }
                if(flag){
                	v.fa=u.num;
                	v.step=u.step+1;
                	v.esti=estimate(v);
                	add_lib(v);
                    q.push(v);
                }
            }
        }
    }
} 

