#include <cstdio>
#include <iostream>
#include <string>
using namespace std;

string a;
string input;
int cnt;

void init_1()
{
    for (int i = 'a'; i <= 'z'; i++)
    {
        if (i == input[0])
        {
            cout << ++cnt << endl;
            return;
        }
        a += i;
        ++cnt;
        a.erase(a.begin(), a.end());
    }
}

void init_2()
{
    for (int i = 'a'; i <= 'z'; i++)
        for (int j = i + 1; j <= 'z'; j++)
        {
            if (i == input[0] && j == input[1])
            {
                cout << ++cnt << endl;
                return;
            }
            a += i;
            a += j;
            ++cnt;
            a.erase(a.begin(), a.end());
        }
}

void init_3()
{
    for (int i = 'a'; i <= 'z'; i++)
        for (int j = i + 1; j <= 'z'; j++)
            for (int k = j + 1; k <= 'z'; k++)
            {
                if (i == input[0] && j == input[1] && k == input[2])
                {
                    cout << ++cnt << endl;
                    return;
                }
                a += i;
                a += j;
                a += k;
                ++cnt;
                a.erase(a.begin(), a.end());
            }
}

void init_4()
{
    for (int l = 'a'; l <= 'z'; l++)
        for (int k = l + 1; k <= 'z'; k++)
            for (int i = k + 1; i <= 'z'; i++)
                for (int j = i + 1; j <= 'z'; j++)
                {
                    if (l == input[0] && k == input[1] && i == input[2] && j == input[3])
                    {
                        cout << ++cnt << endl;
                        return;
                    }
                    a += l;
                    a += k;
                    a += i;
                    a += j;
                    ++cnt;
                    a.erase(a.begin(), a.end());
                }
}

void init_5()
{
    for (int m = 'a'; m <= 'z'; m++)
        for (int l = m + 1; l <= 'z'; l++)
            for (int k = l + 1; k <= 'z'; k++)
                for (int i = k + 1; i <= 'z'; i++)
                    for (int j = i + 1; j <= 'z'; j++)
                    {
                        if (m == input[0] && l == input[1] && k == input[2] && i == input[3] && j == input[4])
                        {
                            cout << ++cnt << endl;
                            return;
                        }
                        a += m;
                        a += l;
                        a += k;
                        a += i;
                        a += j;
                        ++cnt;
                        a.erase(a.begin(), a.end());
                    }
}

void init_6()
{
    for (int n = 'a'; n <= 'z'; n++)
        for (int m = n + 1; m <= 'z'; m++)
            for (int l = m + 1; l <= 'z'; l++)
                for (int k = l + 1; k <= 'z'; k++)
                    for (int i = k + 1; i <= 'z'; i++)
                        for (int j = i + 1; j <= 'z'; j++)
                        {
                            if (n == input[0] && m == input[1] && l == input[2] && k == input[3] && i == input[4] && j == input[5])
                            {
                                cout << ++cnt << endl;
                                return;
                            }
                            a += n;
                            a += m;
                            a += l;
                            a += k;
                            a += i;
                            a += j;
                            ++cnt;
                            a.erase(a.begin(), a.end());
                        }
}
int main()
{
    cin >> input;
    if (input.length() == 1)
    {
        init_1();
        return 0;
    }
    for (int i = 1; i < input.length(); i++)
    {
        if (input[i] <= input[i - 1])
        {
            cout << '0' << endl;
            return 0;
        }
    }
    if (input.length() == 2)
    {
