/*这一部分都是大整数模板代码。*/
struct Bign{
	int D[100000];
	int len;
	Bign(){
		fill(D,D+100000,0);
		len=0;
	}
};
Bign ans;
Bign change(string s){
	Bign a;
	a.len=s.size();
	for(int i=0;i<a.len;i++){
		a.D[i]=s[a.len-i-1]-'0';
	}
	return a;
}
Bign mult(Bign a,int b){
	Bign c;
	int carry=0;
	for(int i=0;i<a.len;i++){
		int tmp=a.D[i]*b+carry;
		c.D[c.len++]=tmp%10;
		carry=tmp/10;
	}
	while(carry!=0){
		c.D[c.len++]=carry%10;
		carry/=10;
	}
	return c;
}
void publish_Bign(Bign a){
	for(int i=a.len-1;i>=0;i--){
		cout<<a.D[i];
	}
}

/*输出*/  
