	for(int i=0;i<F.size();i++){
		if(i)cout<<" ";
		cout<<F[i];
	}
	cout<<endl;
	publish_Bign(ans);
}

/*高精度乘法，计算所有可以选择的数的乘积*/           
void calc(){
	ans=change("1");
	for(int i=0;i<F.size();i++){
		ans=mult(ans,F[i]);
	}
}

/*选择和为n尽可能多的数*/  
void find(){
	int sum=0;
	for(int i=2;i<n;i++){
		if(sum+i>n){
			int rm=n-sum;
			while(rm){
				for(int j=F.size()-1;j>=0&&rm>0;j--){
					F[j]++;
					rm--;
				}
			}
			return ;
		}
		F.push_back(i);
		sum+=i;
	}
}

/*输入*/
