{
	s[++num].w=w;
	s[num].next=head[u];
	head[u]=num;
	s[num].to=v;
}
void spfa(int x)//SPFA经典操（ban）作（zi）
{
	queue<int> q;
	q.push(x);
	for(int i=0;i<=n+1;i++)
		dis[i]=1;
	dis[x]=0;vis[x]=1;
	while(!q.empty())
	{
		int g=q.front();
		q.pop();
		vis[g]=0;
		for(int i=head[g];i!=-1;i=s[i].next)
		{
			int t=s[i].to;
			if(dis[t]>dis[g]+s[i].w)
			{
				dis[t]=dis[g]+s[i].w;
				if(!vis[t])
				{
					q.push(t);
					vis[t]=1;				
				}
			}
		}
	}
}
