void spfa(int s)
{
    memset(used,0,sizeof(used));
    memset(dis,0,sizeof(dis));
    q.push_back(s);
    used[s]=1;
    int k;
    while(!q.empty())
    {
        k=q.front();
        q.pop_front();
        used[k]=0;
        node *p=&head[k];
        while(p->next!=NULL)
        {
            p=p->next;
            if(dis[k]+p->v>dis[p->n])
            {
                dis[p->n]=dis[k]+p->v;
                if(!used[p->n])
                {
                    used[p->n]=1;
                    if(q.empty()||dis[p->n]<dis[q.front()])
                        q.push_front(p->n);
                    else
                        q.push_back(p->n);
