```cpp
#include<bits/stdc++.h>
using namespace std;
queue<int>q;
const int N=8e5+1;
int u[N],v[N],w[N],nxt[N],fst[N],tot,dis[N],flag[N];
int n,m,x,y;
void add(int x,int y,int z)
{
	tot++;
	u[tot]=x;
	v[tot]=y;
	w[tot]=z;
	nxt[tot]=fst[x];
	fst[x]=tot;
}
void dij(int ss)
{
	for(int i=0;i<=n+1;i++)  dis[i]=-200000000;
	dis[ss]=0;
	q.push(ss);
	flag[ss]=1;
	while(!q.empty())
	{
		int x=q.front();
		q.pop();
		flag[x]=0;
		for(int i=fst[x];i!=-1;i=nxt[i])
		{
			int y=v[i],z=w[i];
			if(dis[x]+z>dis[y])
			{
				dis[y]=dis[x]+z;
				if(!flag[y]) q.push(y),flag[y]=1;
			}
		}
	}
}
int main()
{
	memset(fst,-1,sizeof(fst));
	memset(nxt,-1,sizeof(nxt));
	cin>>n>>m;
	for(int i=1;i<=n;i++) add(i,i-1,-1),add(i-1,i,0);
	for(int i=1;i<=m;i++)
	{
		int xx,yy,zz;
