void qsort(int s1,int s2)   //手打快拍从小到大不解释
{
    int mid1=b[(s1+s2)/2];
    int i=s1,j=s2,t;
    while (i<=j)
    {
        while (b[i]<mid1) i++;
        while (b[j]>mid1) j--;
        if (i<=j)
        {
            t=b[i];b[i]=b[j];b[j]=t;
            t=a[i];a[i]=a[j];a[j]=t;
            t=sz[i];sz[i]=sz[j];sz[j]=t;
            i++;j--;
        } 
    }
    if (i<s2) qsort(i,s2);
    if (j>s1) qsort(s1,j);
}
