    l1=a.length();
    int x=0,k;
    for (int i=l1-1;i>=0;i--) {   //两串相加
        k=a[i]-'0'+b[i]-'0'+x;
        x=k/10;
        k%=10;
        ans=char(k+'0')+ans;
    }
    if (x!=0) ans=char(x+'0')+ans;
    return ans;
}
string x,y,z;
int n,i;
int main()
{
    cin>>n;
    if (n==0) {   //没什么必要的特判
        cout<<"0";
        return 0;
    }
    if (n==1 || n==2) {  //还是没什么必要的特判
        cout<<"1";
        return 0;
    }
    //下面是字符串式肥不垃圾数列的初始化
    x="1"+x;  //f[1]=1;
    y="1"+y;  //f[2]=1;
    for (i=3;i<=n;i++) {  
        z=y;   //由于不想搞一个特别大的数组，所以冒了一个泡
        y=jia(x,y);
        x=z;
    }
    cout<<jia(x,y); //最后还要再加一下
    return 0;
}

