因为F（x)=F(x-1)+F(x-2)

所以直接像斐波那契数列那样递推即可

1和2时要人工赋值



















```cpp
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#define max(a,b) (a>b?a:b)
#define min(a,b) (a>b?b:a)
typedef struct IntEx
{
    int *Dat;
    int Long;
    int Max;
    int ZF;
    IntEx()
    {
        Dat=(int*)malloc(100*sizeof(int));
        memset(Dat,0,100*sizeof(int));
        Long=1;
        Max=100;
        ZF=0;
    }
    ~IntEx()
    {
        free(Dat);
    }
    void Alloc();
    int Wish(IntEx*a,int Do);
    void Add(IntEx*a);
    int IsB(IntEx*a);
    void Minus(IntEx*a);
    void Pow10(int hm)
    {
        if(!hm)return;
        while(hm+Long>=Max)Alloc();
        memmove(Dat+hm,Dat,sizeof(int)*Long);
        memset(Dat,0,sizeof(int)*hm);
        Long+=hm;
    }
    void Mul(int a);
    void Select();
    void Multi(IntEx* a);
    void Copy(IntEx*a);
    void Print();
    void Insert(char*l);
    void Minusz(IntEx* a);
}IE,*LPIE;
void IE::Alloc()
{
    int*ND=(int*)malloc(sizeof(int)*(Max+100));
    memset(ND,0,sizeof(int)*(Max+100));
    memcpy(ND,Dat,sizeof(int)*(Max));
    Max+=100;
    free(Dat);
    Dat=ND;
}
int IE::IsB(LPIE a)
{
    if(Long>a->Long)return 1;
    if(Long<a->Long)return 0;
    int wi=Long-1;
    for(;wi>=0;--wi)
        if(Dat[wi]>a->Dat[wi])
            return 1;
        else if(Dat[wi]<a->Dat[wi])
            return 0;
    return 1;
}
int IE::Wish(LPIE a,int d)
{
    if(d==1)
        return max(Long,a->Long)+1;
    if(d==2)
        return Long+a->Long+1;
    return 0;
}
void IE::Select()
{
    while(Long-1&&Dat[Long-1]==0)--Long;
    if(Long==1&&Dat[0]==0)ZF=0;
}
void IE::Add(LPIE a)
{
    int wi=0,exa=0;
    int tLong=Wish(a,1);
    while(Max<tLong)Alloc();
    Long=tLong;
    while(a->Max<Long)a->Alloc();
    for(wi=0;wi<Long;++wi)
    {
        Dat[wi]+=a->Dat[wi]+exa;
        exa=Dat[wi]/10;
        Dat[wi]%=10;
    }
    Select();
}
void IE::Minusz(LPIE a)
{
    while(Long>a->Max)a->Alloc();
    int wi=0,exa=0;
    for(wi=0;wi<Long;++wi)
    {
        Dat[wi]-=exa+a->Dat[wi];
        if(Dat[wi]<0)exa=(Dat[wi]+1)/-10.0+1;
        else exa=0;
        Dat[wi]+=exa*10;
    }
    Select();
}
void IE::Copy(LPIE a)
{
    while(a->Long>Max)Alloc();
    Long=a->Long;
    memset(Dat,0,sizeof(int)*Max);
    memcpy(Dat,a->Dat,sizeof(int)*a->Long);
}
void IE::Mul(int a)
{
    Long+=1;
    while(Long>=Max)Alloc();
    int exa=0,wi;
    for(wi=0;wi<Long;++wi)
    {
        Dat[wi]=Dat[wi]*a+exa;
        exa=Dat[wi]/10;
        Dat[wi]%=10;
    }
    Select();
}
void IE::Multi(LPIE a)
{
    IE s,b;
    int wi;
    for(wi=0;wi<a->Long;++wi)
    {
        b.Copy(this);
    //    b.Print();
        b.Mul(a->Dat[wi]);
        b.Pow10(wi);
        s.Add(&b);
//        s.Print();
//        putchar('\n');
    }
    Copy(&s);
    Select();
    ZF=ZF^a->ZF;
}
void IE::Print()
{
    int wi;
    if(ZF)
        printf("-");
    for(wi=Long-1;wi>=0;--wi)
        printf("%d",Dat[wi]);
}
void IE::Insert(char*s)
{
    int l=strlen(s);
    if(s[0]=='-')
    {
        --l,++s;
        ZF=1;
    }
    Long=l;
    while(Long>=Max)Alloc();
    int wi;
    for(wi=0;wi<Long;++wi)
        Dat[wi]=s[Long-wi-1]-'0';
}
void IE::Minus(LPIE a)
{
    if(ZF^a->ZF)
    {
        Add(a);
        return;
    }
    IE bf;
    if(IsB(a))
    {
        Minusz(a);
    }
    else
    {
        bf.Copy(a);
        bf.Minusz(this);
        Copy(&bf);
        ZF=!ZF;
    }
}
IE Alz[5100];
int main()
{
    int N;
    scanf("%d",&N);
    Alz[1].Dat[0]=1;
    Alz[2].Dat[0]=2;
    int wi;
    for(wi=3;wi<=N;++wi)
    {
        Alz[wi].Copy(Alz+wi-1);
        Alz[wi].Add(Alz+wi-2);
    }
    Alz[N].Print();
    return 0;
}
