//一维高精斐波那契
#include <iostream>
using namespace std;
int a[10001], b[10001], c, p = 1;//因为不知道多少位所以开大一点
void precision(int l)
{
    int x = 0;
    c = 1;//c是当前加时的位数
    if(l % 2 == 1) 
    {
        while(c <= p)//边界
            {
             a[c] = a[c] + b[c] + x;//高精加， 逆序存放
             x = a[c] / 10;
             a[c] = a[c] % 10;
             c++;
        }
        if(x > 0) a[c] = x;//最高位处理
         else c--;
        p = c;//p指针记下每次数组长度， 下次用作边界
    }
    else //同理
    {
        while(c <= p)
        {
            b[c] = a[c] + b[c] + x;
            x = b[c] / 10;
            b[c] = b[c] % 10;
            c++;
        }
        if(x > 0) b[c] = x;
         else c--;
        p = c;
    }
}
int main()
{
    int n, i;
    cin >> n;
    a[1] = 1;//初始化
    b[1] = 2;
    if(n == 0)//0或1或2可以直接输出
    {
        cout << 0;
        return 0;
    }
    if(n == 1)
    {
        cout << 1;
        return 0;
    }
    if(n == 2)
    {
        cout << 2;
        return 0;
    }
    for(i = 3; i <= n; ++i) precision(i);//递推
    if(n % 2 == 1)//核心算法  1个楼梯用a数组表示， 2个楼梯用b数组表述， 3个楼梯用a数组表示， 4个楼梯用b数组表示。
     for(i = c; i >= 1; --i) cout << a[i];//奇数个楼梯用a数组， 偶数个楼梯用b数组， 这样可以保证在高精加的时候每次都是a数组加b数组
    else
     for(i = c; i >= 1; --i) cout << b[i];//n为奇数、偶数时输出不一样的数组， 逆序输出
    return 0;
}
