using namespace std;
inline void read(int &x){
	int f = 1;x = 0;char ch = getchar();
	while(ch < '0' || ch > '9'){if(ch == '-')f = -1;ch = getchar();}
	while(ch >= '0' && ch <= '9'){x = x*10+ch-'0';ch = getchar();}
	x *= f;
}
const int N = 1005,M = 5005;
int head[N],to[M],nxt[M],w[M],cnt;
int dist[N];
int n,m;
bool vis[N];

inline void insert(int x,int y,int z){ to[++cnt] = y; nxt[cnt] = head[x]; head[x] = cnt; w[cnt] = z; }

void SPFA(int x){
	vis[x] = true;
	for(int i = head[x];i;i = nxt[i]){
		int v = to[i];
		if(dist[v] < dist[x]+w[i]){
			if(vis[v]){
				puts("NO SOLUTION");
				exit(0);
			}
			else{
				dist[v] = dist[x]+w[i];
				SPFA(v);
			}
		}
	}
	vis[x] = false;
}

int main(){
	read(n);read(m);
	for(int i = 1,x,y,z;i <= m;++i){
		read(x); read(y); read(z);
		insert(x,y,-z);
	}
	for(int i = 1;i <= n;++i) dist[i] = -1e9;
	for(int i = 1;i <= n;++i){
		if(dist[i] == -1e9) dist[i] = 0;
		SPFA(i);
	}
	int Min = 1e9;
	for(int i = 1;i <= n;++i) Min = min(Min,dist[i]);
	for(int i = 1;i <= n;++i) printf("%d\n", dist[i]-Min);
	return 0;
}
