	node*next;
}*h[MAXN],pool[MAXM];
int dis[MAXN],vis[MAXN],n,m,tot=0,mina=10211314;
bool col[MAXN];
queue <int> q;

void addedge(int u,int v,int c){
	node*p=&pool[++tot];
	p->v=v;p->c=c;p->next=h[u];h[u]=p;
}

bool spfa(){
