		vis[i]=1;
		col[i]=1;
		q.push(i);
	}
	while(!q.empty()){
		int u=q.front();q.pop();
		col[u]=0;
		for(node*p=h[u];p;p=p->next){
			if(dis[p->v]>dis[u]+p->c){
				dis[p->v]=dis[u]+p->c;
				if(!col[p->v]){
					col[p->v]=1;
					vis[p->v]++;
					q.push(p->v);
					if(vis[p->v]>n){
						return 0;
					}
				}
			}
		}
	}
	return 1;
}

