		y++;
		add(x, y, z, zz);
	}
	dijkstrar(1);
	d++;
	int maxx = 0;
	dis[d][maxx] = 1e9;
	for(int i = 1; i <= 500; i++)
	{
		if(dis[d][i] < dis[d][maxx])
		{
			maxx = i;
		}
	}
	write(d, maxx);
	return 0;
}
